import React, { useEffect, useState } from "react";
import axios from "axios";

import './style.scss'
import { Button, Card,CardImg, CardBody, CardTitle, CardText, Form, Label, Input, FormGroup } from "reactstrap";
import Navbar from "../../components/navbar";
import img3 from '../../assets/img/img1.jpg';
import BreadCrumb from "../../components/breadcrumb";
import { useParams } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTags } from "@fortawesome/free-solid-svg-icons";
import { FormSelect } from "react-bootstrap";
const db = require('../../db.json')

export default function SubmitPage() {
    const params = useParams();
    const [data, setData] = useState([]);
    const [club, setClub] = useState([]);
    

    // const getClub = () => {
    //     setClub(db.clubs)
    // };
    const getClub = async () => {
        const response = await axios.get('http://localhost:8000/club')
            setClub(response.data);
    };
    const getData = async () => {
        const response = await axios.get(`http://localhost:8000/news/${params._id}`)
            setData(response.data);
    };

    const initialFormValue = {
        title: "",
        body: "",
        category: "",
        club: "",
        user_id: data._id
    };
    
    const [form, setForm] = useState(initialFormValue);
    
    useEffect(() => {
        getClub();
        getData();
    }, [data]);

    const createData = async () => {
        await axios.post('http://localhost:8000/news', form)
            .then((res) => {
                data.push(form)
            })
            .catch((err) => console.error(err));
    };
    const handleSubmit = (e) => {
        e.preventDefault()
        return createData();

    };
    return(
        <>
            <div className="px-4 py-5 my-5 text-center">
                <h1 className="display-5 fw-bold">Submit news</h1>
                <div className="cccol-lg-6 mx-auto">
                    <p className="lead">Share your knowledge about epl</p>
                </div>
            </div>
            <div className="row justify-content-center">
                <div className="col-8">
                    <Form action="/submit-news" encType="multipart/form-data" method="post" onSubmit={handleSubmit}>
                        <div className="row g-3">
                            <div className="col-12">
                                <Label htmlFor="image" className="form-label">header</Label>
                                <Input type="file" className="form-control" name="image" accept="image/*"/>
                            </div>
                            <div className="col-12">
                                <Label htmlFor="title" className="form-label">title</Label>
                                <Input type="text" name="title" id="title"
                                value={form.title}
                                onChange={(e) => setForm((prev) => ({
                                    ...prev,
                                    title: e.target.value
                                }))}/>
                            </div>
                            <div className="col-12">
                                <Label htmlFor="body" className="form-label">body</Label>
                                <textarea type="text" name="body" id="body" className="form-control" cols={30} row={4}
                                value={form.body}
                                onChange={(e) => setForm((prev) => ({
                                    ...prev,
                                    body: e.target.value
                                }))}/>
                            </div>
                            <div className="col-12">
                                <Label htmlFor="category" className="form-label">category</Label>
                                <Input type="text" name="category" id="category"
                                value={form.category}
                                onChange={(e) => setForm((prev) => ({
                                    ...prev,
                                    category: e.target.value
                                }))}/>
                            </div>
                            <div className="col-12">
                                <Label htmlFor="club" className="form-label">club</Label>
                                <select className="form-select form-control" name="club" aria-label="club">
                                    <option value={form.club}
                                onChange={(e) => setForm((prev) => ({
                                    ...prev,
                                    club: e.target.value
                                }))}> chelsit</option>
                                    {club.map((c) => (
                                        <option value={form.club}
                                        onChange={(e) => setForm((prev) => ({
                                            ...prev,
                                            club: e.target.value
                                        }))}>{c.name}</option>
                                    ))}
                                </select>
                            </div>
                            <div className="col-12">
                                <Button type="submit" className="btn btn-primary" id="addNews">submit news</Button>
                            </div>
                        </div>
                    </Form>
                </div>
            </div>
        </>
    )
}
