import React, { useEffect, useState } from "react";

import './style.scss'
import img2 from '../../assets/img/img2.jpg'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars, faSearch } from "@fortawesome/free-solid-svg-icons";
import { Button, Input } from "reactstrap";
import axios from "axios";
const db = require('../../db.json')

export default function Categories() {
    const [data, setData] = useState([]);
    const [club, setClub] = useState([]);
    const getClub = () => {
        setClub(db.clubs)
    };
    const getData = async () => {
        const response = await axios.get('http://localhost:8000/news')
            setData(response.data);
    };
    const category = [];
    db.news.map((d) => d.category.map((dd) => {if (!category.includes(dd)){category.push(dd)}}))
    
    useEffect(() => {
        getClub();
        getData();
    }, []);
    console.log(data);
    return (
        <div className="category-section">
            <div className="row category-row">
                {category.map((d, idx) => (
                    <a href={`/news/${idx}`} className="col category__link" key={idx}>
                        <div className="category__img shadow">
                            <img src={img2} alt="epl news" loading={"lazy"} />
                        </div>
                        <div className="pt-1">{d}</div>
                    </a>
                ))}
                <a href="/categories" className="col category__link">
                    <div className="category__img shadow">
                        <img src={img2} alt="epl news" loading={"lazy"} />
                    </div>
                    <div className="pt-1">View All</div>
                </a>
            </div>
        </div>

    )
}
