import React, { useEffect, useState } from "react";
import axios from "axios";

import './style.scss'
import { Button, Card,CardImg, CardBody, CardTitle, CardText } from "reactstrap";
import Navbar from "../../components/navbar";
const db = require('../../db.json')

export default function NewsPage() {
    const [data, setData] = useState([]);
    const [newsHeader, setNewsHeader] = useState("");

    const getData = () => {
        setData(db.clubs)
    };
    const handleClub = () => {
        setNewsHeader("club")
    };
    const handleCategory = () => {
        setNewsHeader("category")
    };
    // const getData = async () => {
    //     const response = await axios.get('http://localhost:5000/club')
    //         setData(response.data);
    // };
    
    useEffect(() => {
        getData();
        if (newsHeader=="club") {handleCategory()} else {handleClub()}
    }, []);
    return(
        <>
            < Navbar />
            <section className="px-4 py-5 submit-section">
                <img src={img3} alt="Publish your news" className="d-block mx-auto mb-4 img-fluid" width={566} height={208} loading={"lazy"}/>
                <h1 className="display-5 fw-bold">Publish your news</h1>
                <div className="col-lg-6 mx-auto">
                    <p className="lead mb-4">publish what you know about epl</p>
                    <div className="d-grid gap-2 d-sm-flex justify-content-sm-center submit-btn">
                        <a href="#" className="btn btn-outline-secondary btn-dark btn-lg">Submit News</a>
                    </div>
                </div>
            </section>
        </>
    )
}
