import React, { useEffect, useState } from "react";

import './style.scss'
import img2 from '../../assets/img/img2.jpg'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars, faSearch } from "@fortawesome/free-solid-svg-icons";
import { Button, Input } from "reactstrap";
const db = require('../../db.json')

export default function Categories() {
    const [club, setClub] = useState([]);
    const getClub = () => {
        setClub(db.clubs)
    };
    const category = [];
    db.news.map((d) => d.category.map((dd) => {if (!category.includes(dd)){category.push(dd)}}))
    
    useEffect(() => {
        getClub();
    }, []);
    console.log(category);
    return (
        <>
            <div class="row">
                <a href="#" className="col category__link">
                    <div className="category__img shadow">
                        <img src={img2} alt="epl news" loading="lazy" />
                    </div>
                    <div className="pt-1">Transfer</div>
                </a>
                <a href="#" className="col category__link">
                    <div className="category__img shadow">
                        <img src={img2} alt="epl news" loading="lazy" />
                    </div>
                    <div className="pt-1">Results</div>
                </a>
                <a href="#" className="col category__link">
                    <div className="category__img shadow">
                        <img src={img2} alt="epl news" loading="lazy" />
                    </div>
                    <div className="pt-1">Rumors</div>
                </a>
                <a href="#" className="col category__link">
                    <div className="category__img shadow">
                        <img src={img2} alt="epl news" loading="lazy" />
                    </div>
                    <div className="pt-1">Showbizz</div>
                </a>
                <a href="#" className="col category__link">
                    <div className="category__img shadow">
                        <img src={img2} alt="epl news" loading="lazy" />
                    </div>
                    <div className="pt-1">View All</div>
                </a>
            </div>
        </>

    )
}
