import React, { useEffect, useState } from "react";
import axios from "axios";

import './style.scss'
import { Button, Card,CardImg, CardBody, CardTitle, CardText, Form, Label, Input } from "reactstrap";
import Navbar from "../../components/navbar";
import img3 from '../../assets/img/img1.jpg';
import BreadCrumb from "../../components/breadcrumb";
import { useParams } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTags } from "@fortawesome/free-solid-svg-icons";
const db = require('../../db.json')

export default function SubmitPage() {
    const [data, setData] = useState([]);
    const params = useParams();
    const getData = async () => {
        const response = await axios.get(`http://localhost:8000/news/${params._id}`)
            setData(response.data);
    };

    // const getData = () => {
    //     setData(db.clubs)
    // };

    const initialFormValue = {
        title: "",
        body: 0,
        category: 0,
        club: "",
    };
    
    const [form, setForm] = useState(initialFormValue);
    
    useEffect(() => {
        getData();
    }, [data]);

    const createData = async () => {
        await axios.post('http://localhost:8080/products', form)
            .then((res) => {
                data.push(form)
            })
            .catch((err) => console.error(err));
    };
    const handleSubmit = (e) => {
        e.preventDefault()
        return createData();

    };
    return(
        <>
            <div className="px-4 py-5 my-5 text-center">
                <h1 className="display-5 fw-bold">Submit news</h1>
                <div className="cccol-lg-6 mx-auto">
                    <p className="lead">Share your knowledge about epl</p>
                </div>
            </div>
            <div className="row justify-content-center">
                <div className="col-8">
                    <Form action="/submit-news" encType="multipart/form-data" method="post" onSubmit={handleSubmit}>
                        <div className="row g-3">
                            {Object.keys(form).map((key,idx) => (
                                <div key={idx}>
                                    <FormGroup>
                                        <Label>
                                            {key}
                                        </Label>
                                        <Input
                                        value={form[key]}
                                        onChange={(e) => setForm((prev) => ({
                                            ...prev,
                                            [key]: e.target.value
                                        }))}/>
                                    </FormGroup>
                                </div>
                            ))}
                            <div className="col-12">
                                <Button type="submit" className="btn btn-primary" id="addNews">submit news</Button>
                            </div>
                        </div>
                    </Form>
                </div>
            </div>
        </>
    )
}
