import React, { useEffect, useState } from "react";

import './style.scss'
import { Button, Card,CardImg, CardBody, CardTitle, CardText } from "reactstrap";
import Navbar from "../../components/navbar";
import img2 from '../../assets/img/img1.jpg'
const db = require('../../db.json')

export default function NewsListPage() {
    
    const category = [];
    db.news.map((d) => d.category.map((dd) => {if (!category.includes(dd)){category.push(dd)}}))
    
    return(
        <div className=" news-list-page">
            < Navbar />
            <div className="row category-row">
                {category.map((d,idx) => (
                    <a href={`/news/${idx}`} className="col category__link" key={idx}>
                        <div className="category__img shadow">
                            <img src={img2} alt="epl news" loading={"lazy"} />
                        </div>
                        <div className="pt-1">{d}</div>
                    </a>
                ))}
                <a href="/categories" className="col category__link">
                    <div className="category__img shadow">
                        <img src={img2} alt="epl news" loading={"lazy"} />
                    </div>
                    <div className="pt-1">View All</div>
                </a>
            </div>
        </div>
    )
}
